// ============================================================================
// INPUT VALIDATION & CAROUSEL MEDIA SETUP - IG Carousel Universal
// ============================================================================

const input = $json;

// ============================================================================
// 1. INPUT VALIDATION
// ============================================================================

const requiredFields = ['platform', 'mediaType', 'media', 'client_identifier', 'instagram_business_account_id'];
const missingFields = [];

requiredFields.forEach(field => {
  if (!input[field]) {
    missingFields.push(field);
  }
});

if (missingFields.length > 0) {
  return [{
    json: {
      error: true,
      error_type: "validation_failed",
      message: `Missing required fields: ${missingFields.join(', ')}`,
      execution_status: "failed",
      client: input.client_identifier || "unknown",
      platform: "IG Carousel"
    }
  }];
}

// ============================================================================
// 2. PLATFORM VALIDATION
// ============================================================================

if (input.platform !== 'IG Carousel') {
  return [{
    json: {
      error: true,
      error_type: "platform_mismatch",
      message: `Platform mismatch: ${input.platform}. Expected: IG Carousel`,
      execution_status: "failed",
      client: input.client_identifier
    }
  }];
}

// ============================================================================
// 3. IG CAROUSEL MEDIA PROCESSING
// ============================================================================

const mediaArray = input.media || [];

if (!Array.isArray(mediaArray) || mediaArray.length === 0) {
  return [{
    json: {
      error: true,
      error_type: "no_media",
      message: "No media provided for IG Carousel",
      execution_status: "failed",
      client: input.client_identifier,
      platform: "IG Carousel"
    }
  }];
}

// Filter for images only
const carouselImages = mediaArray.filter(file => {
  const isImage = file.type?.toLowerCase().includes('image') ||
                 ['jpg', 'jpeg', 'png', 'webp'].some(ext => 
                   file.filename?.toLowerCase().includes(`.${ext}`)
                 );
  return isImage;
});

// Carousel validation
if (carouselImages.length < 2) {
  return [{
    json: {
      error: true,
      error_type: "insufficient_images",
      message: "IG Carousel requires at least 2 images",
      execution_status: "failed",
      client: input.client_identifier,
      platform: "IG Carousel"
    }
  }];
}

if (carouselImages.length > 10) {
  return [{
    json: {
      error: true,
      error_type: "too_many_images",
      message: "IG Carousel supports maximum 10 images",
      execution_status: "failed",
      client: input.client_identifier,
      platform: "IG Carousel",
      note: `Found ${carouselImages.length} images, using first 10`
    }
  }];
}

// ============================================================================
// 4. EXECUTION CONTEXT SETUP
// ============================================================================

const executionContext = {
  execution_id: input.execution_context?.execution_id || input.execution_id || `ig_carousel_${Date.now()}`,
  client_name: input.client_name || input.client_identifier,
  client_identifier: input.client_identifier,
  table_id: input.table_id,
  record_id: input.record_id,
  original_record_id: input.original_record?.id || input.record_id,
  started_at: new Date().toISOString(),
  timeout_ms: input.timeout_ms || 180000,
  max_retries: input.max_retries || 3
};

// ============================================================================
// 5. IG CAROUSEL CONFIGURATION (DYNAMIC PAGE ID)
// ============================================================================

const carouselConfig = {
  instagram_business_account_id: input.instagram_business_account_id, // FROM CLIENT REGISTRY
  api_version: "v23.0",
  media_type: "CAROUSEL",
  access_token_field: "instagram_access_token"
};

console.log(`[${executionContext.execution_id}] IG Carousel for ${executionContext.client_name} - Page ID: ${carouselConfig.instagram_business_account_id}`);

// ============================================================================
// 6. CHILD CONTAINERS BATCH PREPARATION (DYNAMIC PAGE ID)
// ============================================================================

const childRequests = carouselImages.slice(0, 10).map((image, index) => ({
  method: "POST",
  relative_url: `v23.0/${carouselConfig.instagram_business_account_id}/media`, // DYNAMIC
  body: `image_url=${encodeURIComponent(image.url)}&is_carousel_item=true`,
  name: `child_${index}`
}));

// ============================================================================
// 7. SUCCESS OUTPUT
// ============================================================================

return [{
  json: {
    validation_passed: true,
    ready_for_posting: true,
    workflow_step: "create_child_containers",

    execution_context: executionContext,

    content: {
      images: carouselImages.slice(0, 10).map((img, index) => ({
        url: img.url,
        filename: img.filename || `carousel_image_${index + 1}.jpg`,
        type: img.type,
        size: img.size,
        id: img.id,
        width: img.width,
        height: img.height,
        index: index
      })),
      caption: input.caption || '',
      mediaType: 'carousel',
      total_images: Math.min(carouselImages.length, 10),
      api_endpoint: 'batch_children_only'
    },

    batch_config: {
      requests: childRequests,
      total_requests: childRequests.length,
      request_type: "child_containers_only"
    },

    parent_config: {
      caption: input.caption || '',
      expected_children_count: Math.min(carouselImages.length, 10),
      instagram_business_account_id: carouselConfig.instagram_business_account_id // FOR PARENT CREATION
    },

    instagram_config: carouselConfig,

    original_input: {
      table_id: input.table_id,
      record_id: input.record_id,
      total_media_files: mediaArray.length,
      carousel_images_count: carouselImages.length,
      images_used: Math.min(carouselImages.length, 10)
    },

    debug: {
      input_received_at: new Date().toISOString(),
      media_files_received: mediaArray.length,
      carousel_images_found: carouselImages.length,
      carousel_images_used: Math.min(carouselImages.length, 10),
      image_filenames: carouselImages.slice(0, 10).map(img => img.filename),
      child_requests_created: childRequests.length,
      expected_next_step: "batch_child_creation_only",
      page_id: carouselConfig.instagram_business_account_id
    }
  }
}];

// PARSE CHILDREN RESPONSE
// Extract child container IDs from batch response and prepare for parent creation

const batchResponse = $json;
const validationData = $('Input Validation & Context Setup').item.json;
const expectedChildren = validationData.content.total_images;

// ============================================================================
// 1. VALIDATE BATCH RESPONSE STRUCTURE
// ============================================================================

if (!Array.isArray(batchResponse)) {
  return [{
    json: {
      error: true,
      error_type: "invalid_children_response",
      message: "Children batch response is not an array",
      raw_response: batchResponse,
      execution_status: "failed"
    }
  }];
}

if (batchResponse.length !== expectedChildren) {
  return [{
    json: {
      error: true,
      error_type: "incorrect_children_count",
      message: `Expected ${expectedChildren} child responses, got ${batchResponse.length}`,
      expected: expectedChildren,
      received: batchResponse.length,
      execution_status: "failed"
    }
  }];
}

// ============================================================================
// 2. EXTRACT CHILD CONTAINER IDS
// ============================================================================

const childContainerIds = [];
const failedChildren = [];

batchResponse.forEach((response, index) => {
  if (!response || response.code !== 200) {
    failedChildren.push({
      index: index,
      code: response?.code,
      error: response?.body ? JSON.parse(response.body) : "No response body"
    });
  } else {
    try {
      const parsedBody = JSON.parse(response.body);
      if (parsedBody.id) {
        childContainerIds.push(parsedBody.id);
      } else {
        failedChildren.push({
          index: index,
          error: "No ID in response body",
          body: parsedBody
        });
      }
    } catch (parseError) {
      failedChildren.push({
        index: index,
        error: "JSON parse failed",
        body: response.body
      });
    }
  }
});

// ============================================================================
// 3. CHECK FOR FAILURES
// ============================================================================

if (failedChildren.length > 0) {
  return [{
    json: {
      error: true,
      error_type: "child_containers_failed",
      message: `${failedChildren.length} out of ${expectedChildren} child containers failed`,
      failed_children: failedChildren,
      successful_children: childContainerIds,
      execution_status: "partial_failure"
    }
  }];
}

if (childContainerIds.length !== expectedChildren) {
  return [{
    json: {
      error: true,
      error_type: "missing_child_ids",
      message: `Expected ${expectedChildren} child IDs, got ${childContainerIds.length}`,
      child_ids: childContainerIds,
      execution_status: "failed"
    }
  }];
}

// ============================================================================
// 4. PREPARE PARENT CONTAINER DATA
// ============================================================================

// Create comma-separated list of child IDs for parent container
const childrenParameter = childContainerIds.join(',');

// ============================================================================
// 5. SUCCESS OUTPUT - READY FOR PARENT CREATION
// ============================================================================

return [{
  json: {
    // Success indicators
    children_created_successfully: true,
    ready_for_parent_creation: true,
    workflow_step: "create_parent_container",

    // Child container data
    child_container_ids: childContainerIds,
    children_count: childContainerIds.length,
    children_parameter: childrenParameter, // For parent container creation

    // Parent container configuration
    parent_container_config: {
      media_type: "CAROUSEL",
      children: childrenParameter,
      caption: validationData.parent_config.caption,
      instagram_business_account_id: "17841470796910825",
      api_version: "v23.0"
    },

    // Preserve execution context
    execution_context: validationData.execution_context,

    // Preserve original content data
    content: validationData.content,

    // Debug information
    debug: {
      children_parsed_at: new Date().toISOString(),
      child_container_ids: childContainerIds,
      children_parameter_length: childrenParameter.length,
      next_step: "create_parent_container",
      parent_will_reference: `children=${childrenParameter}`
    }
  }
}];

// ============================================================================
// WORKFLOW STATUS - Pass Through Tier 3 Result
// ============================================================================
// Purpose: Tier 3 already formatted everything, just pass it through
// ============================================================================

const tier3Result = $json; // From Tier 3 Publisher

console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
console.log(`‚úÖ CAROUSEL WORKFLOW - COMPLETE`);
console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

if (tier3Result.success) {
  console.log(`üìä Publication Summary:`);
  console.log(`   ‚Ä¢ Platform: ${tier3Result.platform}`);
  console.log(`   ‚Ä¢ Permalink: ${tier3Result.publication_data.permalink}`);
  console.log(`   ‚Ä¢ Media ID: ${tier3Result.publication_data.published_media_id}`);
  console.log(`   ‚Ä¢ Client: ${tier3Result.execution_context.client_name}`);
  console.log(`‚úÖ Carousel published successfully!`);
}

// Pass through Tier 3 result unchanged
return [{ json: tier3Result }];

// ============================================================================
// PREPARE PUBLISHER INPUT - For Tier 3
// ============================================================================
// Purpose: Format container data for IG Container Publisher
// ============================================================================

const parentContainer = $json; // From Parent Container Creation
const inputData = $('Input Validation & Context Setup').item.json;

console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
console.log(`üì¶ CAROUSEL - PREPARING FOR TIER 3`);
console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

// ============================================================================
// VALIDATE PARENT CONTAINER
// ============================================================================

if (!parentContainer || !parentContainer.id) {
  throw new Error('CAROUSEL_FAILED: Parent container creation did not return container ID');
}

console.log(`‚úÖ Parent Container ID: ${parentContainer.id}`);

// ============================================================================
// BUILD TIER 3 INPUT
// ============================================================================

const tier3Input = {
  // Required for Tier 3
  container_id: parentContainer.id,
  instagram_business_account_id: inputData.instagram_config.instagram_business_account_id,
  platform: "IG Carousel",
  wait_duration: 10, // Carousel uses 10 seconds

  // Execution context (pass through)
  execution_context: inputData.execution_context,

  // Content information
  content: {
    caption: inputData.content.caption || '',
    media_type: "CAROUSEL",
    total_images: inputData.content.total_images
  },

  // Client config for notifications (pass through if exists)
  client_config: inputData.client_config || {}
};

console.log(`üìä Tier 3 Input Prepared:`);
console.log(`   ‚Ä¢ Container ID: ${tier3Input.container_id}`);
console.log(`   ‚Ä¢ Platform: ${tier3Input.platform}`);
console.log(`   ‚Ä¢ Page ID: ${tier3Input.instagram_business_account_id}`);
console.log(`   ‚Ä¢ Wait Duration: ${tier3Input.wait_duration}s`);
console.log(`   ‚Ä¢ Total Images: ${tier3Input.content.total_images}`);
console.log(`   ‚Ä¢ Client: ${tier3Input.execution_context.client_name}`);

console.log(`‚úÖ Ready to call Tier 3 Publisher`);

return [{ json: tier3Input }];

// ============================================================================
// MINIMAL ERROR HANDLER - Extract client + pass to Error Support
// ============================================================================

const error = $json;
const workflowInfo = $workflow;
const executionInfo = $execution;

// Extract error message
let errorMessage = "Unknown error occurred";
if (typeof error === 'string') {
  errorMessage = error;
} else if (error?.message) {
  errorMessage = error.message;
} else {
  errorMessage = JSON.stringify(error);
}

// Get client from webhook (if available)
let clientName = "unknown";
try {
  const webhookData = $('Extract Webhook Data')?.item?.json;
  if (webhookData?.client_name) {
    clientName = webhookData.client_name;
  }
} catch (e) {
  // No webhook data available
}

// Simple payload for Error Support
return {
  error_message: errorMessage,
  error_stack: error.stack || null,
  workflow_name: workflowInfo.name,
  workflow_id: workflowInfo.id,
  execution_id: executionInfo.id,
  client_name: clientName, // ‚Üê Just this from webhook
  timestamp: new Date().toISOString()
};

