// Get webhook context and client config
const webhookContext = $('Extract Webhook Data').item.json;
const input = $json; // Contains client registry data

console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
console.log(`üîç SHARING PERMISSION & MEDIA VALIDATION`);
console.log(`   Execution ID: ${webhookContext.execution_id}`);
console.log(`   Client: ${webhookContext.client_name}`);
console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

// Create execution log entry
const executionLog = {
  execution_id: webhookContext.execution_id,
  workflow_name: "Posting Router",
  step: "validation_and_preparation",
  started_at: new Date().toISOString(),
  client_name: webhookContext.client_name,
  table_id: webhookContext.table_id,
  record_id: webhookContext.record_id,
  triggered_by: webhookContext.triggered_by,
  execution_type: webhookContext.execution_type
};

try {
  // ============================================================================
  // 1. EXTRACT RECORD DATA FROM WEBHOOK
  // ============================================================================

  const recordData = webhookContext.original_webhook?.record_data || webhookContext.record_data;

  if (!recordData) {
    throw new Error('VALIDATION_FAILED: No record_data found in webhook payload');
  }

  console.log(`üì¶ Record data received from webhook`);

  // ============================================================================
  // 2. EXTRACT RECORD FIELDS
  // ============================================================================

  const aksiyon = recordData.aksiyon;
  const payla≈üƒ±mT√ºr√º = recordData.platform;
  const medya = recordData.media || [];
  const content = recordData.caption || '';
  const scheduledTime = recordData.scheduled_time || '';

  console.log(`üìã Field values:`);
  console.log(`   Aksiyon: ${aksiyon}`);
  console.log(`   Platform: ${payla≈üƒ±mT√ºr√º}`);
  console.log(`   Media count: ${medya.length}`);
  console.log(`   Caption length: ${content.length} chars`);

  // ============================================================================
  // 3. RECORD VALIDATION
  // ============================================================================

  console.log(`üîç Starting validation checks...`);

  // Check 1: Record Status
  if (aksiyon !== 'Payla≈üƒ±ma Hazƒ±r') {
    throw new Error(`VALIDATION_FAILED: Record not ready for posting. Current Aksiyon: ${aksiyon}`);
  }
  console.log(`   ‚úÖ Status check passed`);

  // Check 2: Platform Specified
  if (!payla≈üƒ±mT√ºr√º) {
    throw new Error(`VALIDATION_FAILED: No platform specified (Payla≈üƒ±m t√ºr√º is empty)`);
  }
  console.log(`   ‚úÖ Platform check passed`);

  // Check 3: Media Files Present
  if (!medya || medya.length === 0) {
    throw new Error(`VALIDATION_FAILED: No media files attached`);
  }
  console.log(`   ‚úÖ Media check passed`);

  // Check 4: Platform Support
  const supportedPlatforms = ["IG Story", "IG Feed", "IG Reels", "IG Carousel"];
  if (!supportedPlatforms.includes(payla≈üƒ±mT√ºr√º)) {
    throw new Error(`VALIDATION_FAILED: Unsupported platform: ${payla≈üƒ±mT√ºr√º}. Supported: ${supportedPlatforms.join(', ')}`);
  }
  console.log(`   ‚úÖ Platform support check passed`);

  console.log(`‚úÖ All validation checks passed for ${payla≈üƒ±mT√ºr√º} post`);

  // ============================================================================
  // 4. MEDIA TYPE DETECTION
  // ============================================================================

  console.log(`üé¨ Detecting media type...`);

  function detectMediaType(file) {
    if (!file) return 'unknown';

    const type = file.type?.toLowerCase() || '';
    const filename = file.filename?.toLowerCase() || '';

    // Check by MIME type first
    if (type.includes('video') || type.includes('mp4') || type.includes('mov')) {
      return 'video';
    }

    if (type.includes('image') || type.includes('jpg') || type.includes('jpeg') || type.includes('png')) {
      return 'image';
    }

    // Check by file extension
    if (/\.(mp4|mov|avi|webm)$/i.test(filename)) {
      return 'video';
    }

    if (/\.(jpg|jpeg|png|gif|webp)$/i.test(filename)) {
      return 'image';
    }

    return 'unknown';
  }

  // Detect media type from first file
  const primaryMediaType = detectMediaType(medya[0]);

  console.log(`   Primary media: ${primaryMediaType}`);
  console.log(`   File: ${medya[0].filename}`);
  console.log(`   Type: ${medya[0].type}`);

  // ============================================================================
  // 5. MEDIA ARRAY PROCESSING
  // ============================================================================

  const processedMedia = medya.map((file, index) => {
    const mediaType = detectMediaType(file);

    console.log(`   Media ${index + 1}/${medya.length}: ${file.filename} (${mediaType})`);

    return {
      id: file.id,
      url: file.url,
      filename: file.filename,
      size: file.size,
      type: file.type,
      mediaType: mediaType,
      width: file.width,
      height: file.height,
      thumbnails: file.thumbnails
    };
  });

  // ============================================================================
  // 6. BUILD OUTPUT DATA
  // ============================================================================

  console.log(`üì§ Building output data for Platform Router...`);

  const outputData = {
    // ===== EXECUTION CONTEXT =====
    execution_context: {
      execution_id: webhookContext.execution_id,
      client_name: webhookContext.client_name,
      client_identifier: webhookContext.client_identifier,
      table_id: webhookContext.table_id,
      record_id: webhookContext.record_id,
      triggered_by: webhookContext.triggered_by,
      execution_type: webhookContext.execution_type,
      started_at: executionLog.started_at,
      timeout_ms: 120000,
      max_retries: 3
    },

    // ===== CONTENT DATA =====
    platform: payla≈üƒ±mT√ºr√º,
    mediaType: primaryMediaType,
    media: processedMedia,
    caption: content,
    scheduled_time: scheduledTime,

    // ===== INSTAGRAM CONFIGURATION (from Client Registry) =====
    instagram_business_account_id: input.instagram_business_account_id,
    instagram_page_name: input.instagram_page_name,

    // ===== TELEGRAM CONFIGURATION (from Client Registry) =====
    telegram_channel_id: input.telegram_channel_id,
    telegram_admin_chat_id: input.telegram_admin_chat_id,
    notifications_enabled: input.notifications_enabled,

    // ===== OPERATIONAL METADATA =====
    client_timezone: input.client_timezone,
    client_active: input.client_active,
    account_type: input.account_type,

    // ===== VALIDATION STATUS =====
    validation_passed: true,
    validation_completed_at: new Date().toISOString(),

    // ===== PIPELINE STATUS =====
    pipeline_status: {
      validation_passed: true,
      ready_for_posting: true,
      should_continue: true,
      error_occurred: false
    },

    // ===== DEBUG INFO =====
    debug: {
      media_count: processedMedia.length,
      primary_media_type: primaryMediaType,
      caption_length: content.length,
      has_scheduled_time: !!scheduledTime
    }
  };

  // ============================================================================
  // 7. FINAL LOGGING
  // ============================================================================

  console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
  console.log(`‚úÖ VALIDATION SUCCESSFUL`);
  console.log(`   Platform: ${payla≈üƒ±mT√ºr√º}`);
  console.log(`   Media Type: ${primaryMediaType}`);
  console.log(`   Media Files: ${processedMedia.length}`);
  console.log(`   Instagram Account: ${input.instagram_business_account_id}`);
  console.log(`   Ready for: Platform Router`);
  console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

  return [{ json: outputData }];

} catch (error) {
  // ============================================================================
  // ERROR HANDLING & LOGGING
  // ============================================================================

  const errorLog = {
    ...executionLog,
    completed_at: new Date().toISOString(),
    status: "validation_failed",
    error_message: error.message,
    error_type: error.message.includes('VALIDATION_FAILED') ? 'validation_error' : 'system_error',
    failed_at_step: "validation_and_preparation"
  };

  console.error(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
  console.error(`‚ùå VALIDATION FAILED`);
  console.error(`   Execution: ${webhookContext.execution_id}`);
  console.error(`   Error: ${error.message}`);
  console.error(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

  // Return error data for error handling workflow
  const errorData = {
    execution_context: webhookContext,
    pipeline_status: {
      validation_passed: false,
      ready_for_posting: false,
      should_continue: false,
      error_occurred: true
    },
    error_details: errorLog,

    // For error workflow routing
    table_id: webhookContext.table_id,
    client_name: webhookContext.client_name,
    client_identifier: webhookContext.client_identifier
  };

  // Re-throw error to trigger error path in workflow
  throw new Error(`PIPELINE_VALIDATION_FAILED: ${error.message}`);
}

// ============================================================================
// EXTRACT WEBHOOK DATA - Enhanced Version
// ============================================================================
// Purpose: Parse, validate, and enrich webhook payload from Airtable
// Improvements:
// - Better error handling with specific error codes
// - More robust field validation
// - Enhanced logging for debugging
// - Turkish character normalization
// - ISO timestamp standardization
// ============================================================================

const webhookData = $json.body;
const currentTime = new Date().toISOString();

// ============================================================================
// HELPER FUNCTIONS
// ============================================================================

/**
 * Validate and clean client name
 * Client names come pre-normalized from Airtable (lowercase, no spaces)
 * We just validate and trim whitespace
 */
function validateClientName(name) {
  if (!name || typeof name !== 'string') {
    return null;
  }

  const cleaned = name.trim().toLowerCase();

  // Allow Turkish characters (≈ü, ƒü, √º, ƒ±, √∂, √ß) plus alphanumeric and underscores
  if (!/^[a-z≈üƒü√ºƒ±√∂√ß0-9_]+$/.test(cleaned)) {
    console.warn(`‚ö†Ô∏è  Client name "${name}" contains unexpected characters`);
  }

  return cleaned;
}

/**
 * Generate unique execution ID
 * Format: exec_[timestamp]_[random]
 */
function generateExecutionId() {
  const timestamp = Date.now();
  const random = Math.random().toString(36).substring(2, 11);
  return `exec_${timestamp}_${random}`;
}

/**
 * Validate required fields
 */
function validateRequiredFields(data) {
  const required = {
    table_id: data.table_id,
    record_id: data.record_id,
    client_name: data.client_name,
    action: data.action,
    triggered_by: data.triggered_by
  };

  const missing = [];
  const empty = [];

  for (const [field, value] of Object.entries(required)) {
    if (value === undefined || value === null) {
      missing.push(field);
    } else if (typeof value === 'string' && value.trim() === '') {
      empty.push(field);
    }
  }

  return { missing, empty, valid: missing.length === 0 && empty.length === 0 };
}

// ============================================================================
// MAIN PROCESSING
// ============================================================================

try {
  // Generate execution ID first for logging
  const executionId = generateExecutionId();

  console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
  console.log(`üöÄ WEBHOOK RECEIVED [${executionId}]`);
  console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

  // ============================================================================
  // STEP 1: VALIDATE WEBHOOK STRUCTURE
  // ============================================================================

  if (!webhookData) {
    throw new Error('WEBHOOK_EMPTY: No data received in webhook body');
  }

  console.log(`üì¶ Webhook data received:`, JSON.stringify(webhookData, null, 2));

  // ============================================================================
  // STEP 2: EXTRACT CORE FIELDS
  // ============================================================================

  const tableId = webhookData.table_id;
  const recordId = webhookData.record_id;
  const clientName = webhookData.client_name;
  const action = webhookData.action;
  const triggeredBy = webhookData.triggered_by;
  const executionType = webhookData.execution_type || 'scheduled';
  const webhookTimestamp = webhookData.timestamp || currentTime;

  // ============================================================================
  // STEP 3: VALIDATE REQUIRED FIELDS
  // ============================================================================

  const validation = validateRequiredFields(webhookData);

  if (!validation.valid) {
    const errorDetails = [];

    if (validation.missing.length > 0) {
      errorDetails.push(`Missing fields: ${validation.missing.join(', ')}`);
    }

    if (validation.empty.length > 0) {
      errorDetails.push(`Empty fields: ${validation.empty.join(', ')}`);
    }

    throw new Error(`WEBHOOK_VALIDATION_FAILED: ${errorDetails.join(' | ')}`);
  }

  console.log(`‚úÖ Field validation passed`);

  // ============================================================================
  // STEP 4: VALIDATE AND CLEAN CLIENT NAME
  // ============================================================================

  const clientIdentifier = validateClientName(clientName);

  if (!clientIdentifier) {
    throw new Error(`CLIENT_NAME_INVALID: Could not validate client name "${clientName}"`);
  }

  console.log(`üè∑Ô∏è  Client validated: "${clientIdentifier}"`);

  // ============================================================================
  // STEP 5: BACKEND STATUS CHECK - DUPLICATE PREVENTION
  // ============================================================================

  console.log(`üîí Checking Backend Status for duplicate execution...`);

  const recordData = webhookData.record_data;

  if (!recordData) {
    throw new Error(`VALIDATION_FAILED: No record_data found in webhook payload`);
  }

  const backendStatus = recordData.backend_status;

  console.log(`   Backend Status: "${backendStatus}"`);

  // Check 1: Already being processed by another execution
  if (backendStatus === 'Is sharing') {
    console.warn(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
    console.warn(`‚ö†Ô∏è  DUPLICATE EXECUTION DETECTED`);
    console.warn(`   Record ${recordId} is already being processed`);
    console.warn(`   Backend Status: "${backendStatus}"`);
    console.warn(`   Action: Stopping execution immediately`);
    console.warn(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

    throw new Error('DUPLICATE_EXECUTION: Another execution is already processing this record');
  }

  // Check 2: Already successfully shared
  if (backendStatus === 'Shared') {
    console.warn(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
    console.warn(`‚ö†Ô∏è  ALREADY SHARED`);
    console.warn(`   Record ${recordId} was already shared`);
    console.warn(`   Backend Status: "${backendStatus}"`);
    console.warn(`   Action: Stopping execution immediately`);
    console.warn(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

    throw new Error('ALREADY_SHARED: This record was already successfully shared');
  }

  // Check 3: Validate expected status
  if (backendStatus !== 'Will be shared') {
    console.error(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
    console.error(`‚ùå UNEXPECTED BACKEND STATUS`);
    console.error(`   Expected: "Will be shared"`);
    console.error(`   Found: "${backendStatus}"`);
    console.error(`   Action: Stopping execution`);
    console.error(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

    throw new Error(`INVALID_STATUS: Expected "Will be shared", got "${backendStatus}"`);
  }

  console.log(`   ‚úÖ Status check passed - safe to proceed`);

  // ============================================================================
  // STEP 6: LOG EXECUTION DETAILS
  // ============================================================================

  console.log(`üìã Execution details:`);
  console.log(`   Table ID: ${tableId}`);
  console.log(`   Record ID: ${recordId}`);
  console.log(`   Action: ${action}`);
  console.log(`   Triggered by: ${triggeredBy}`);
  console.log(`   Execution type: ${executionType}`);
  console.log(`   Timestamp: ${webhookTimestamp}`);

  // ============================================================================
  // STEP 6: BUILD OUTPUT OBJECT
  // ============================================================================

  const output = {
    // ===== EXECUTION TRACKING =====
    execution_id: executionId,
    webhook_received_at: currentTime,
    workflow_name: "The Posting Router V2",
    entry_point: "Extract Webhook Data",

    // ===== WEBHOOK PAYLOAD DATA =====
    table_id: tableId,
    record_id: recordId,
    action: action,
    triggered_by: triggeredBy,
    execution_type: executionType,
    timestamp: webhookTimestamp,

    // ===== CLIENT IDENTIFICATION =====
    client_name: clientName,              // From Airtable (e.g., "kurtulu≈ü")
    client_identifier: clientIdentifier,  // Validated/cleaned (e.g., "kurtulu≈ü")

    // ===== METADATA =====
    validation_status: "passed",
    processed_at: currentTime,

    // ===== ORIGINAL DATA (for debugging) =====
    original_webhook: webhookData,

    // ===== PIPELINE STATUS =====
    pipeline_status: {
      validation_passed: true,
      ready_for_next_step: true,
      should_continue: true,
      error_occurred: false
    }
  };

  // ============================================================================
  // STEP 7: FINAL LOGGING
  // ============================================================================

  console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);
  console.log(`‚úÖ WEBHOOK EXTRACTION SUCCESSFUL`);
  console.log(`   Execution ID: ${executionId}`);
  console.log(`   Client: ${clientName} (${clientIdentifier})`);
  console.log(`   Record: ${recordId}`);
  console.log(`   Triggered by: ${triggeredBy}`);
  console.log(`‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ`);

  return [{ json: output }];

} catch (error) {
  // ============================================================================
  // ERROR HANDLING
  // ============================================================================

